void GLWidget::passColors( QVector<QColor> myColors )       {   _myColors = myColors;   }

void GLWidget::passColorIndexes( QVector<int> myColorIndex )
{
        _myColorIndex  = myColorIndex;

        myColor_Mesh1    = _myColors[ myColorIndex[0]  ];
      //                 = _myColors[ myColorIndex[1]  ];
      //                 = _myColors[ myColorIndex[2]  ];
        myColor_Left     = _myColors[ myColorIndex[3]  ];
        myColor_Right    = _myColors[ myColorIndex[4]  ];
        myColor_Aux1     = _myColors[ myColorIndex[5]  ];
        myColor__Hand    = _myColors[ myColorIndex[6]  ];
        myColor_Aux      = _myColors[ myColorIndex[7]  ];
        myColor__0       = _myColors[ myColorIndex[8]  ];
        myColor__1       = _myColors[ myColorIndex[9]  ];
        myColor__2       = _myColors[ myColorIndex[10] ];
      //                 = _myColors[ myColorIndex[11] ];
      //                 = _myColors[ myColorIndex[12] ];
        myColor_Mesh2    = _myColors[ myColorIndex[13] ];
        myColor_Mesh3    = _myColors[ myColorIndex[14] ];
}


